/**
 *
 * An array instance is valid against `minItems` if its size is greater than, or equal to, the value of this keyword.
 *
 * !> The value `minItems` MUST be a non-negative integer.
 *
 * ?> Omitting this keyword has the same behavior as a value of 0.
 *
 * ## Example
 *
 * ```typescript
 * class Model {
 *    @PropertyType(String)
 *    @MinItems(10)
 *    property: string[];
 * }
 * ```
 *
 * Will produce:
 *
 * ```json
 * {
 *   "type": "object",
 *   "properties": {
 *     "property": {
 *       "type": "number",
 *       "minItems": 10
 *     }
 *   }
 * }
 * ```
 *
 * @param {number} minItems
 * @returns {Function}
 * @decorator
 * @ajv
 * @jsonschema
 */
export declare function MinItems(minItems: number): (...parameters: any[]) => any;
